 */
class FestivalEdition extends BaseFestivalEdition
{
	public function  getUrlKey() {
		if ($this->_get('url_key') == '') {
			return Doctrine_Inflector::urlize($this->getName());
		} else {
			return $this->_get('url_key');
		}
	}

	public static function getInstance()
	{
		return Doctrine_Core::getTable('FestivalEdition');
	}

	public function getName()
	{
		return $this->getFestival()->getName() . ' - ' . $this->getEdition();
	}
	
	public function preDelete($event)
	{
		// Delete the big file and the thumbnail
		unlink(sfConfig::get('app_festival_edition_path') . '/' . $event->getInvoker()->getFilename());
		unlink(sfConfig::get('app_festival_edition_path') . '/t-' . $event->getInvoker()->getFilename());
		unlink(sfConfig::get('app_festival_edition_path') . '/ts-' . $event->getInvoker()->getFilename());

		return parent::preDelete($event);
	}
	
	public function createFile()
	{
		$sourceFile = sfConfig::get('app_festival_edition_path') . '/' . $this->getFilename();
		
		if (!file_exists($sourceFile)){
			throw new sfException('Source file not available: ' . $sourceFile);
		}

		/* Create the big file */
		$photo = new sfThumbnail(sfConfig::get('app_festival_edition_sourceimage_width'), sfConfig::get('app_festival_edition_sourceimage_height'), true, false, 100);
		$photo->loadFile($sourceFile);
		$photo->save(sfConfig::get('app_festival_edition_path') . '/' . $this->getFilename());

		/* Create the thumbnail */
		$thumb = new sfThumbnail(sfConfig::get('app_festival_edition_thumbnail_width'), sfConfig::get('app_festival_edition_thumbnail_height'), true, false, 100);
		$thumb->loadFile($sourceFile);
		$thumb->save(sfConfig::get('app_festival_edition_path') . '/t-' . $this->getFilename());

		/* Create the small thumbnail */
		$thumb = new sfThumbnail(sfConfig::get('app_festival_edition_thumbnail_small_width'), sfConfig::get('app_festival_edition_thumbnail_small_height'), true, false, 100);
		$thumb->loadFile($sourceFile);
		$thumb->save(sfConfig::get('app_festival_edition_path') . '/ts-' . $this->getFilename());
	}

	public function getCountComments()
	{
		return Doctrine_Core::getTable('Comment')->getCountByEntity('FestivalEdition', $this->getLibraryId());
	}

	public function getFirstPhotos($limit = null)
	{
		return Doctrine_Query::create()
			->from('Photo p')
			->limit($limit)
			->where('p.album_id = ?', $this->getPhotoAlbumId())
			->orderBy('p.position')
			->execute();
	}

	public function getFirstVideos($limit = null)
	{
		return Doctrine_Query::create()
			->from('Video v')
			->limit($limit)
			->where('v.album_id = ?', $this->getVideoAlbumId())
			->orderBy('v.position')
			->execute();
	}

	public function getNewestArticles($limit = null)
	{
		return ArticleTable::getInstance()->getNewestByFestivalEdition($this->getId(), $limit);
	}

	public function getRelatedStires($limit = null, $page = null, $returnArray = true)
    {
		return StireTable::getInstance()->getRelatedByFestivalEdition($this->getId(), $limit, $page, $returnArray);
    }

	public function getRelatedStiresCount()
    {
		return StireTable::getInstance()->getRelatedByFestivalEditionCount($this->getId());
    }

	public function getJudges()
	{

	}
}