class FestivalSectionTable extends Doctrine_Table
{
	public function getByFestivalEditionAndImdbKey($festivalEditionId, $imdbKey)
	{
		return Doctrine_Query::create()
			->from('FestivalSection fs')
			->where('fs.festival_edition_id = ? AND fs.imdb_key = ?', array($festivalEditionId, $imdbKey))
			->fetchOne();
